import React, { useState, useEffect } from 'react';
import CartList from './CartList';

function App() {
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState('');

  useEffect(() => {
    // Fetch data from the server and update the state
    fetch('http://localhost:3008/api/cart')
      .then((response) => response.json())
      .then((data) => { setCart(data); console.log(data) })
      .catch((error) => console.error(error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:3008/api/cart/delete/${id}`, { method: 'POST' })
      .then((response) => response.json())
      .then((data) => { setCart(data); console.log(data) })
      .catch((error) => console.error(error));
  };

  const handleUpdate = (id, updatedData) => {
    // Assuming updatedData is an object representing the updated information for the cart item
    fetch(`http://localhost:3008/api/cart/update/${id}`, {
      method: 'PATCH', // Use 'PATCH' if it's a partial update
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedData),
    })
      .then((response) => response.json())
      .then((data) => {
        // Update the cart with the updated data from the server
        const updatedCart = cart.map((item) => {
          if (item.id === id) {
            return { ...item, ...updatedData };
          }
          return item;
        });

        setCart(updatedCart);
        console.log(data);
      })
      .catch((error) => console.error(error));

  const handleSubmit = (e) => {
    e.preventDefault();
    // Assuming newItem is an object representing a cart item
    setCart([...cart, newItem]);
    setNewItem('');
  };

  return (
    <div>
      <h1>Cart Items</h1>
      <CartList cart={cart} onDelete={handleDelete} onUpdate={handleUpdate} />

      <h2>Add Item to Cart</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Type an item"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
        />
        <button type="submit">Add Item to Cart</button>
      </form>
    </div>
  );
}
}
export default App;
