import React, { useState, useEffect } from 'react';
import CartList from './CartList';

function App() {
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState('');
  const [editingItemId, setEditingItemId] = useState(null); // State to track the item being edited

  useEffect(() => {
    // Fetch data from the server and update the state
    fetch('http://localhost:3008/api/cart')
      .then((response) => response.json())
      .then((data) => { setCart(data); console.log(data) })
      .catch((error) => console.error(error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:3008/api/cart/delete/${id}`, {
      method: 'DELETE', // Use the DELETE method for deleting the item
    })
      .then((response) => response.json())
      .then((data) => { setCart(data); console.log(data) })
      .catch((error) => console.error(error));
  };

  const handleUpdate = (id, updatedData) => {
    fetch(`http://localhost:3008/api/cart/update/${id}`, {
      method: 'PUT', // Use the PUT method for updating the item
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedData),
    })
      .then((response) => response.json())
      .then((data) => {
        setCart(data);
        setEditingItemId(null); // Clear the editing state after a successful update
        console.log(data);
      })
      .catch((error) => console.error(error));
  };

  const handleEdit = (id) => {
    setEditingItemId(id); // Set the editing state to the item's ID when editing is initiated
  };

  const handleCancelEdit = () => {
    setEditingItemId(null); // Clear the editing state when editing is canceled
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Assuming newItem is an object representing a cart item
    setCart([...cart, newItem]);
    setNewItem('');
  };

  return (
    <div>
      <h1>Cart Items</h1>
      <CartList
        cart={cart}
        onDelete={handleDelete}
        onUpdate={handleUpdate}
        onEdit={handleEdit}
        onCancelEdit={handleCancelEdit}
        editingItemId={editingItemId}
      />

      <h2>Add Item to Cart</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Type an item"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
        />
        <button type="submit">Add Item to Cart</button>
      </form>
    </div>
  );
}

export default App;
