import React, { useState, useEffect } from 'react';
import CartList from './CartList';

function App() {
  const [cart, setCart] = useState([]);
  const [newItem, setNewItem] = useState('');
  const [updateInput, setUpdateInput] = useState(''); // State for the update input
  const [editingItemId, setEditingItemId] = useState(null); // State for the item being edited

  useEffect(() => {
    // Fetch data from the server and update the state
    fetch('http://localhost:3008/api/cart')
      .then((response) => response.json())
      .then((data) => {
        setCart(data);
        console.log(data);
      })
      .catch((error) => console.error(error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:3008/api/cart/delete/${id}`, {
      method: 'POST',
    })
      .then((response) => response.json())
      .then((data) => {
        setCart(data);
        console.log(data);
      })
      .catch((error) => console.error(error));
  };

  const handleUpdate = (id, updatedData) => {
    fetch(`http://localhost:3008/api/cart/update/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedData),
    })
      .then((response) => response.json())
      .then((data) => {
        setCart(data);
        setEditingItemId(null); // Clear the editing state after a successful update
        console.log(data);
      })
      .catch((error) => console.error(error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Assuming newItem is an object representing a cart item
    setCart([...cart, newItem]);
    setNewItem('');
  };

  return (
    <div>
      <h1>Cart Items</h1>
      <CartList
        cart={cart}
        onDelete={handleDelete}
        onUpdate={handleUpdate}
        editingItemId={editingItemId}
        setEditingItemId={setEditingItemId}
      />

      <h2>Add Item to Cart</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Type an item"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
        />
        <button type="submit">Add Item to Cart</button>
      </form>

      {editingItemId !== null && (
        <div>
          <h2>Edit Item</h2>
          <input
            type="text"
            value={updateInput}
            onChange={(e) => setUpdateInput(e.target.value)}
            placeholder="Enter new item text"
          />
          <button
            onClick={() => {
              handleUpdate(editingItemId, { text: updateInput });
              setUpdateInput(''); // Clear the input field
            }}
          >
            Update
          </button>
          <button onClick={() => setEditingItemId(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default App;
